
public class ArraySum {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		// Sum of Array

		int sum = 0;
		int c[] = { 1, 2, 3, 4, 5, 6, 6 };
		for (int i = 0; i < c.length; i++) {
			// System.out.println(c[i]);
			sum = sum + c[i];

		}

		System.out.println("Total Sum is " + sum);

	}

}
